p_record_id M_INOUT.M_INOUT_ID%TYPE;
p_docAction M_INOUT.DocAction%TYPE;

TYPE RECORD IS REF CURSOR;
Cur_Params RECORD;
Cur_Orders RECORD;

BEGIN

  FOR Cur_Params IN (
    SELECT *
    FROM ad_ep_instance_para
    WHERE ad_ep_instance_id = p_ep_instance_id
    ) LOOP
    IF (cur_params.parametername LIKE 'DocAction') THEN
      p_docaction := Cur_Params.p_string;
    ELSIF (cur_params.parametername LIKE 'Record_ID') THEN
      p_record_id := cur_params.p_string;
    END IF;
  END LOOP;
  IF(p_docaction = 'CO') THEN
    FOR Cur_Orders IN (
      select c_order.c_order_id
      from c_order
      join c_orderline on c_order.c_order_id = c_orderline.c_order_id
      join m_inoutline on m_inoutline.c_orderline_id = c_orderline.c_orderline_id
      join m_inout on m_inoutline.m_inout_id = m_inout.m_inout_id
      where c_order.em_obpos_islayaway = 'Y'
      and m_inout.docstatus = 'CO'
      and m_inoutline.m_inout_id = p_record_id
      ) LOOP
      RAISE_APPLICATION_ERROR(-20000, '@OBPOS_Layaway_No_Shipment@');
    END LOOP;
  END IF;
END OBPOS_DELIVER_LAYAWAY
