BEGIN
IF AD_isTriggerEnabled()='N' THEN IF DELETING THEN RETURN OLD; ELSE RETURN NEW; END IF; END IF;
IF DELETING OR INSERTING OR coalesce(:new.ad_businessunit_org_id,'NULL') != coalesce(:old.ad_businessunit_org_id,'NULL') OR coalesce(:new.ad_calendarowner_org_id,'NULL') != coalesce(:old.ad_calendarowner_org_id,'NULL') OR coalesce(:new.ad_legalentity_org_id,'NULL') != coalesce(:old.ad_legalentity_org_id,'NULL') OR coalesce(:new.ad_periodcontrolallowed_org_id,'NULL') != coalesce(:old.ad_periodcontrolallowed_org_id,'NULL') THEN UPDATE ad_cache SET last_invalidation = now() WHERE value = 'OrganizationStructure'; END IF;
END AD_ORG_CACHE_TRG
