BEGIN
  IF AD_isTriggerEnabled()='N' THEN RETURN;
  END IF;

  IF (INSERTING OR UPDATING) THEN
    UPDATE M_PRODUCT SET Updated=:new.Updated, UpdatedBy=:new.UpdatedBy 
    WHERE M_PRODUCT_ID = :new.M_PRODUCT_ID;
  END IF;

END OBPOS_PRODUCT_CH_VALUE_TRG
